import React from 'react';
import styled from 'styled-components';
const CardBlock = ({ title, content, color }) => {
  const Content = styled.div`
    font-weight: bold;
    font-size: 1.75rem;
    line-height: 2rem;
    margin-bottom: 20px;
    color: ${color};
  `;
  return (
    <>
      <Content>{content}</Content>
    </>
  );
};

export default CardBlock;
