import { React, useState } from 'react';
import Card from '../components/Card';
import CardInput from '../components/CardInput';
import styled from 'styled-components';
import { useDispatch } from 'react-redux';
import { createWordBook } from '../redux/modules/wordBook';
import { Link } from 'react-router-dom';
const AddPage = () => {
  const [form, setForm] = useState({});
  const dispatch = useDispatch();

  const handleChange = (e) => {
    // setForm({
    //   ...form,
    //   [e.target.name]: e.target.value,
    // });
    dispatch(createWordBook({ [e.target.name]: e.target.value }));
  };

  return (
    <>
      <h3>단어 추가하기</h3>
      <Card>
        <CardInput title='단어' handleChange={handleChange}></CardInput>
      </Card>

      <Card>
        <CardInput title='설명' handleChange={handleChange}></CardInput>
      </Card>
      <Card>
        <CardInput title='예시' handleChange={handleChange}></CardInput>
      </Card>
      <Link>
        <Button>추가하기</Button>
      </Link>
    </>
  );
};
const Button = styled.div`
  padding: 10px;

  text-align: center;
  background-color: rgb(97, 0, 255);
  color: white;

  cursor: pointer;
`;
export default AddPage;
