import React from 'react';
import Card from '../components/Card';
import CardBlock from '../components/CardBlock';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
const MainPage = () => {
  // const data = [
  //   { word: 'word', desc: 'desc', example: 'example' },
  //   { word: 'word', desc: 'desc', example: 'example' },
  //   { word: 'word', desc: 'desc', example: 'example' },
  // ];
  const data = useSelector((state) => state);
  return (
    <>
      <h3>MY DICTIONNARY</h3>
      {data.map((item, i) => (
        <Card key={i}>
          <CardBlock title='단어' content={item.word} color='black' />
          <CardBlock title='설명' content={item.desc} color='black' />
          <CardBlock title='예시' content={item.example} color='skyblue' />
        </Card>
      ))}

      <Link to='add'>
        <Button>+</Button>
      </Link>
    </>
  );
};
const Button = styled.button`
  width: 5rem;
  height: 5rem;

  background-color: rgb(97, 0, 255);
  color: white;
  font-weight: bold;
  font-size: 4.5rem;
  border-radius: 100%;

  cursor: pointer;

  border: none;
  position: sticky;

  bottom: 0rem;
  left: 15rem;
`;

export default MainPage;
