import React from 'react';
import Card from '../components/Card';
import CardBlock from '../components/CardBlock';
import styled, { keyframes } from 'styled-components';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
const MainPage = () => {
  const myLists = useSelector((state) => state.wordBook.list);

  return (
    <>
      <h3>MY DICTIONNARY</h3>
      {myLists.map((item, i) => (
        <Card key={i}>
          <CardBlock title='단어' content={item.word} color='black' />
          <CardBlock title='설명' content={item.desc} color='black' />
          <CardBlock title='예시' content={item.example} color='skyblue' />
        </Card>
      ))}

      <Link to='add'>
        <Button>+</Button>
      </Link>
    </>
  );
};
const boxAnimation = keyframes`
0%{border-radius: 0px;}
50%{border-radius: 25px;}
100%{border-radius: 50px;}
`;
const Button = styled.button`
  width: 5rem;
  height: 5rem;

  background-color: rgb(97, 0, 255);
  color: white;
  font-weight: bold;
  font-size: 4.5rem;
  border-radius: 50px;

  cursor: pointer;

  border: none;
  position: absolute;

  bottom: 2rem;
  left: 15rem;

  animation: boxAnimation 1s 2s infinite linear;
`;

export default MainPage;
