import React, { useState } from 'react';
import styled from 'styled-components';
const CardInput = (props) => {
  const [form, setForm] = useState({
    title: props.title,
    text: '',
  });

  const handleChange = (e) => {
    const nextForm = {
      ...form,
      text: e.target.value,
    };
    setForm(nextForm);
  };

  return (
    <div>
      <UnderLine>{props.title}</UnderLine>
      <Content value={form.value} onChange={handleChange} />
    </div>
  );
};

const UnderLine = styled.div`
  text-decoration: underline;
  font-size: 0.5rem;
  margin-bottom: 10px;
`;

const Content = styled.input`
  font-size: 1.125rem;
  line-height: 2rem;
  width: 100%;
  margin-bottom: 5px;
  margin-right: 10px;
  box-sizing: border-box;
  border: 1.5px solid;
`;
export default CardInput;
