import React from 'react';
import styled from 'styled-components';
import Content from './content';
const Card = ({ word, desc }) => {
  const Background = styled.div`
    background-color: white;
    height: 350px;
    width: 350px;
    margin-bottom: 20px;
    padding: 10px;
  `;
  const UnderLine = styled.div`
    text-decoration: underline;
    font-size: 0.5rem;
  `;
  const Content = styled.div`
    font-weight: bold;
    font-size: 1.5rem;
    line-height: 2rem;
    margin-bottom: 20px;
  `;
  return (
    <>
      <Background>
        <Content title='단어' content='{word}' color='black' />
        <Content title='설명' content='{desc}' color='black' />
        <Content
          title='예시'
          content='저 친구가 초콜릿을 줬어. ㅎ1ㅎ1'
          color='skyblue'
        />

        <UnderLine>예시</UnderLine>
      </Background>
    </>
  );
};

export default Card;
