import React from 'react';
import styled from 'styled-components';
import CardBlock from './CardBlock';
const Card = ({ word, desc, example }) => {
  const Background = styled.div`
    background-color: white;
    height: 300px;
    width: 400px;
    margin-bottom: 20px;
    padding: 10px;
  `;
  const Button = styled.Button``;
  return (
    <>
      <Background>
        <CardBlock title='단어' content={word} />
        <CardBlock title='설명' content={desc} />
        <CardBlock title='예시' content={example} />
        <Button />
      </Background>
    </>
  );
};

export default Card;
